arguments.argument:
colBlock = instance_place(x, y + 1, objBlock)
isRightFree = !place_meeting(x + 1, y, objBlock)
isLeftFree = !place_meeting(x - 1, y, objBlock)
playerOnLeft = collision_line(x - 1, y, x - 1, y + 32, player, true, false)
playerOnRight = collision_line(x + 32, y, x + 32, y + 32, player, true, false)

if (colBlock != noone) {
    gravity = 0
    vspeed = 0
    y = colBlock.y - 32
    if (isRightFree && isLeftFree) {
        if (playerOnLeft) {
            hspeed = spd
            event_user(0)
        }
        if (playerOnRight) {
            hspeed = -spd
            event_user(0)
        }
    }
} else {
    gravity = grav * player.yflag
    if (speed > maxVspeed) {
        speed = maxVspeed
    }
}

if (place_meeting(x, y, player) && vspeed != 0) {
    with (player) {
        killPlayer()
    }
}
if (hspeed > frc) {
    hspeed -= frc
} else if (hspeed < -frc) {
    hspeed += frc
} else {
    hspeed = 0
}
