arguments.argument:
isPlayerOnPlatform =
    collision_rectangle(
        bbox_left - 2,
        bbox_top - 2,
        bbox_right + 2,
        bbox_bottom + 2,
        player,
        0,
        1
    ) != noone && player.onPlatform == true


if (isPlayerOnPlatform && isTouchPlatform && !sent) {
    if (spd != 0) {
        hspeed = lengthdir_x(spd, dir)
        vspeed = lengthdir_y(spd, dir)
    } else {
        hspeed = h
        vspeed = v
    }
    ns_send_instance(
        2, 
        'h', hspeed, 
        'v', vspeed
    )
    sent = true 
}
