arguments.argument:
// collides with player
if (place_meeting(x, y, player) && vspeed != 0) {
    with (player) {
        killPlayer()
    }
}

// movable with platform
if (colBlock != noone) {
    if (
        (colBlock.hspeed != 0 || colBlock.vspeed != 0) &&
        !place_meeting(x + colBlock.hspeed, y + colBlock.vspeed, block)
    ) {
        x += colBlock.hspeed
        y += colBlock.vspeed
    }
}
