arguments.argument:
///line attack

sprite_index = sprCherryPink
//stop moving
move = false
// this will make the 'shoot_count' variable loop like this:
// 0,1,2,3,4,5,6,7,0,1,2,3,4,,5,6,7,0,1,...
shoot_count = (shoot_count + 1) mod 8
//aims player at the beginning of the 8 bullets
if ((shoot_count = 0)) {
    dir = point_direction(x, y, player.x, player.y)
}
// create bullets
inst = instance_create(x, y, objBossBullet)
inst.speed = 12
inst.direction = dir
inst.sprite_index = sprite_index
// becomes rng during second phase
if (buff) {
    inst.alarm[2] = 28 + irandom(4)
}

//play sound effect
audio_playsound(sndShoot)
//shoot timer
if (shoot_count < 7) {
    alarm[7] = 2
} else {
    alarm[7] = 20
}
