arguments.argument:
// destroy the bullet
with (other) {
    instance_destroy()
}
// when the boss is not invincible
if (flash == 0 && curHP != 0) {
    // trigger visual effects
    event_user(0)
    // update boss status
    event_user(1)
    // tell other players we hit the boss
    ns_send_event('boss_hit', 1, 'id', id)
}
