arguments.argument:
// boss status when bullet hit boss

// decrease current HP by 1
curHP -= 1
// if the boss is going to die
if (curHP <= 0) {
    //stop all the alarms
    for (i = 0; i <= 11; i += 1) {
        alarm[i] = 0
    }
    //destroy the HP bar
    with (objHPBar) {
        instance_destroy()
    }
    //change sprite back
    sprite_index = sprCherryGray
    //stop moving
    move = false
    //stop wind
    windy = false
    //start spamming before death
    alarm[9] = 50
    //screen shake
    alarm[10] = 50
}
