arguments.argument:
// scaling trigger
if (tarx != 0) {
    if (time == 0) {
        if (tarx * (image_xscale - (tarx - xsp)) < 0) {
            image_xscale += xsp
            x -= (cx - xoff) * xsp
        } else {
            image_xscale = tarx
            x = xstart - (cx - xoff) * (tarx - xs)
        }
    } else {
        if (tarx * (image_xscale - tarx) < 0) {
            image_xscale += (tarx - 1) / time
            x -= (cx - xoff) * (tarx - xs) / time
        }
    }
}
if (tary != 0) {
    if (time == 0) {
        if (tary * (image_yscale - (tary - ysp)) < 0) {
            image_yscale += ysp
            y -= (cy - yoff) * ysp
        } else {
            image_yscale = tary
            y = ystart - (cy - yoff) * (tary - ys)
        }
    } else {
        if (tary * (image_yscale - tary) < 0) {
            image_yscale += (tary - 1) / time
            y -= (cy - yoff) * (tary - ys) / time
        }
    }
}

alarm[11] = 1
