arguments.argument:
if (pth != 0) {
    // this is a path killer
    path_start(pth, spd, enda, 0)
    if (scl != 0) {
        path_scale = scl
    }
} else if (h != 0 || v != 0 || spd != 0 || dir != 0) {
    // this is a moving killer
    if (spd != 0) {
        hspeed = lengthdir_x(spd, dir)
        vspeed = lengthdir_y(spd, dir)
    } else {
        hspeed = h
        vspeed = v
    }
}
