arguments.argument:
// auto tile blocks outside view
var x1, y1, x2, y2;

x1 = xx
y1 = yy
x2 = xx + view_wview[0]
y2 = yy + view_hview[0]

with (objBlock) {
    if (object_index == objBlock && x >= x1 && x <= x2 && y >= y1 && y <= y2) {
        autotile(
            other.dir,
            other.tile,
            other.size,
            other.depth,
            other.sep,
            other.row
        )
    }
}

xx += view_wview[0]
if (xx >= room_width) {
    xx = 0
    yy += view_hview[0]
}
if (yy >= room_height) {
    alarm[0] = 0
} else {
    alarm[0] = 1
}
