arguments.argument:
if (background_exists(tile)) {
    width = background_get_width(tile)
    height = background_get_height(tile)
    if (
        width >= 32 &&
        width < 48 &&
        height >= 64 &&
        height < 80 &&
        width != height
    ) {
        dir = 2
        sep = height - 64
    } else if (
        width >= 48 &&
        width < 128 &&
        height >= 48 &&
        height < 128 &&
        width == height
    ) {
        dir = 4
        sep = (width - 48) / 3
    } else if (
        width >= 48 &&
        width < 256 &&
        height >= 80 &&
        height < 192 &&
        width != height
    ) {
        dir = 8
        sep = (width - 48) / 3
    } else if (width >= 128 && height >= 128 && width == height) {
        dir = 4
        size = 32
        sep = (width - 128) / 3
    } else if (width >= 256 && height >= 192) {
        dir = 8
        size = 32
        sep = (width - 128) / 7
    }
    if (instance_exists(player)) {
        px = clamp(player.x, 0, room_width - 1)
        py = clamp(player.y, 0, room_height - 1)
        view_xview[0] = floor(px / view_wview[0]) * view_wview[0]
        view_yview[0] = floor(py / view_hview[0]) * view_hview[0]
    }
    with (objBlock) {
        if (object_index == objBlock && !outsideView()) {
            autotile(
                other.dir,
                other.tile,
                other.size,
                other.depth,
                other.sep,
                other.row
            )
        }
    }
    xx = 0
    yy = 0
    alarm[0] = 1
} else if (!global.enable_production_mode) {
    show_error('Please specify an appropriate tile. ' + error_info(), 0)
}
