arguments.argument:
// calculating views according to player position
if (instance_exists(player)) {
    xx = clamp(player.x + 1, 0, room_width - 1)
    yy = clamp(player.y + 1, 0, room_height - 1)
    view_xview[0] = floor(xx / view_wview[0]) * view_wview[0]
    view_yview[0] = floor(yy / view_hview[0]) * view_hview[0]
}
