arguments.argument:
if (instance_exists(player)) {
    //set this object as the view object
    view_object[0] = id
    //follow player smoothly
    hspeed = (player.x - x) / 16
    vspeed = (player.y - y) / 12
    if (player.x <= x + 2 && player.x >= x - 2) {
        hspeed = 0
    }
    if (player.y <= y + 2 && player.y >= y - 2) {
        vspeed = 0
    }
} else {
    vspeed = 0
    hspeed = 0
}
