arguments.argument:
if (saveTimer > 0) {
    saveTimer -= 1
}
if (
    place_meeting(x, y, bullet) ||
    (place_meeting(x, y, player) && keyboard_check_pressed(global.shootbutton))
) {
    if (saveTimer <= 0 && instance_exists(player) == true) {
        saveTimer = 30
        image_index = 2
        alarm[0] = 80
        ns_send_instance()
        saveGame()
    }
}
