arguments.argument:
if (saveTimer > 0) {
    saveTimer -= 1
}
if (
    place_meeting(x, y, bullet) ||
    (place_meeting(x, y, player) && keyboard_check_pressed(global.shootbutton))
) {
    if (saveTimer <= 0 && instance_exists(player)) {
        saveTimer = 30
        image_index = 2
        alarm[0] = 80
        saveGame()
        // sync save
        ns_send_event(
            'save_sync', 7,
            'id', id,
            'room', room,
            'x', player.x,
            'y', player.y,
            'xs', player.image_xscale,
            'rev', global.reverse,
            'scope', SCOPE_OTHERS
        )
    }
}
