arguments.argument:
if (saveTimer > 0) {
    saveTimer -= 1
}
if (
    place_meeting(x, y, bullet) ||
    (place_meeting(x, y, player) && keyboard_check_pressed(global.shootbutton))
) {
    if (saveTimer <= 0 && instance_exists(player) && image_index != 2) {
        saveTimer = 30
        image_index = 1
        if (!ns_is_online_mode()) {
            saveGame()
            image_index = 2
            alarm[0] = 80
        } else {
            ns_send_wait(
                'save', -1, 3,
                'id', id,
                'x', player.xprevious,
                'y', player.yprevious
            )
        }
    }
}
