arguments.argument:
if (fetched) {
    roomCount = ds_list_size(lobbies)
    if (offset >= roomCount && offset > 0) {
        offset -= size
    }
}

if (keyboard_check_pressed(global.jumpbutton)) {
    music_play(sndJump)
    if (global.game_mode == MODE_USER_SELECT) {
        mode = MODE_COOPERATION
    } else {
        mode = global.game_mode
    }

    if (debug_mode) {
        cmd = cmd_init(
            'create_lobby', 2, 
            'name', 'debug',
            'mode', mode
        )
        ns_send(cmd)
    } else {
        ns_create_lobby()
    }
}

if (keyboard_check_pressed(global.shootbutton)) {
    music_play(sndDJump)
    ns_logout()
}
