arguments.argument:
//锟絁锟絒锟絓锟斤拷锟节擄拷
if (keyboard_check_pressed(global.leftbutton) == true) {
    music_play(sndJump)
    select -= 1
    if (select < 1) {
        select = 3
    }
} else if (keyboard_check_pressed(global.rightbutton) == true) {
    music_play(sndJump)
    select += 1
    if (select > 3) {
        select = 1
    }
} else if (keyboard_check_pressed(global.jumpbutton) == true) {
    global.savenum = select
    room_goto(rSelectStage)
}
