arguments.argument:
draw_set_color(c_black)

for (i = 1; i <= 3; i += 1) {
    // draw "Data x"
    draw_set_halign(fa_left)
    draw_set_font(font24)
    draw_text(x + 21 + (i - 1) * 239, y + 9, str[i])
    //draw deaths & time
    draw_set_font(font12)
    draw_text(x + (i - 1) * 239 + 10, y + 70, 'death:' + string(menu_death[i]))
    timeStr =
        'time:' + string(hour[i]) + ':' + string(muni[i]) + ':' + string(sec[i])
    draw_text(x + (i - 1) * 239 + 10, y + 90, timeStr)
    draw_set_halign(fa_center)
    if (difselect == 1) {
        drawX = x + (global.savenum - 1) * 239 + 65
        drawY = y + 49
        if (difname == -1) {
            draw_text(drawX, drawY, '< Load game >')
        } else {
            draw_text(drawX, drawY, '<' + difText[difname] + '>')
        }
    }
    if (file_exists(getSaveFile(i))) {
        if (difselect == 0 || (difselect == 1 && i != global.savenum)) {
            draw_text(
                x + (i - 1) * 239 + 65,
                y + 49,
                difText[menu_difficulty[i]]
            )
        }
        draw_set_font(font24)
        //draw clear
        if ((menu_clear[i] = 1)) {
            draw_text(x + (i - 1) * 239 + 65, y + 215, 'Clear!!')
        }
        //draw icon
        for (j = 1; j <= 8; j += 1) {
            if (menu_boss[i, j] && sprite_exists(bossicon[j])) {
                draw_sprite(
                    bossicon[j],
                    -1,
                    x + (i - 1) * 239 + 32 * ((j - 1) mod 4),
                    y + 128 + 32 * ((j - 1) div 4)
                )
            }
        }
    }
    //draw the cherry indicator
    if (i == select) {
        draw_sprite(sprCherry, -1, x + (i - 1) * 239 + 5, y + 310)
        draw_sprite(sprCherry, -1, x + (i - 1) * 239 + 25, y + 310)
        draw_sprite(sprCherry, -1, x + (i - 1) * 239 + 45, y + 310)
        draw_sprite(sprPlayerRunning, -1, x + (i - 1) * 239 + 67, y + 312)
        draw_sprite(sprCherry, -1, x + (i - 1) * 239 + 85, y + 310)
        draw_sprite(sprCherry, -1, x + (i - 1) * 239 + 105, y + 310)
        draw_sprite(sprCherry, -1, x + (i - 1) * 239 + 125, y + 310)
    }
}
