arguments.argument:
if (difselect == 0) {
    if (keyboard_check_pressed(global.leftbutton)) {
        music_play(sndJump)
        select -= 1
        if (select < 1) {
            select = 3
        }
    } else if (keyboard_check_pressed(global.rightbutton)) {
        music_play(sndJump)
        select += 1
        if (select > 3) {
            select = 1
        }
    } else if (keyboard_check_pressed(global.jumpbutton)) {
        global.savenum = select
        difselect = 1
        difname = 0
        if (file_exists(getSaveFile(global.savenum))) {
            difname = -1
        }
    }
} else {
    if (keyboard_check_pressed(global.leftbutton)) {
        music_play(sndJump)
        difname -= 1
        if ((difname = -1)) {
            if (!file_exists(getSaveFile(global.savenum))) {
                difname = 3
            }
        }
        if (difname < -1) {
            difname = 3
        }
    } else if (keyboard_check_pressed(global.rightbutton)) {
        music_play(sndJump)
        difname += 1
        if (difname > 3) {
            difname = -1
        }
        if ((difname = -1)) {
            if (!file_exists(getSaveFile(global.savenum))) {
                difname = 0
            }
        }
    } else if (keyboard_check_pressed(global.shootbutton)) {
        global.savenum = 0
        difselect = 0
    } else if (keyboard_check_pressed(global.jumpbutton)) {
        dif = difname
        if (dif < 0) {
            room_goto(rLoad)
        } else {
            global.difficulty = dif
            if (file_exists(getSaveFile(global.savenum))) {
                file_delete(getSaveFile(global.savenum))
            }
            global.death = 0
            global.timestep = 0
            scrWarpRoom(global.first_stage)
        }
    }
}
