arguments.argument:
if (succeed) {
    exit;
} else {
    alarm[0] = 50
}

startTime[times] = date_current_time()

debug('udp shake hand ...............')

if (udpsocket_get_state(global.udpSockId) == 2) {
    udpsocket_reset(global.udpSockId)
    udpsocket_start(global.udpSockId)
}

// send udp handshake packet
buffer_clear(global.udpBufId)
buffer_write_int8(global.udpBufId, CMD_TYPE_INIT)
buffer_write_int8(global.udpBufId, times)
buffer_write_string(global.udpBufId, global.__player_id)
udpsocket_send(global.udpSockId, global.udpBufId);
buffer_clear(global.udpBufId)
