arguments.argument:
if (succeed) {
    exit;
} else {
    alarm[0] = 50
}

startTime[times] = date_current_time()

debug('udp shake hand ...............')

// send udp handshake packet
clearbuffer()
writebyte(0)
writebyte(times)
writestring(global.__player_id)
sendmessage(global.udpSockId, global.__ip, global.__udp_port);
clearbuffer()
