arguments.argument:
if (_room != room or dead) {
    exit;
}

drawSelf()
draw_set_color(c_white)
draw_set_font(font12)
draw_set_halign(fa_center)
draw_set_valign(fa_bottom)
if (is_string(name)) {
  if (global.reverse) {
    if (reverse) {
      draw_text_transformed_outline(x, y + 14, name, 1, 1, angle - 180)
    } else {
      draw_text_transformed_outline(x, y - 32, name, 1, 1, angle - 180)
    }
  } else {
    if (reverse) {
      draw_text_transformed_outline(x, y + 32, name, 1, 1, angle)
    } else {
      draw_text_transformed_outline(x, y - 14, name, 1, 1, angle)
    }
  }

}
