arguments.argument:
image_index = 0

message_caption(1, text)
switch(text) {
    case "register":
        ns_register();
        break;
    case "login":
        ns_login();
        break;
    case "logout":
        ns_logout();
        break;
    case "create_lobby":
        ns_create_lobby();
        break;
    case "join_lobby":
        ns_join_lobby();
        break;
    case "leave_lobby":
        ns_leave_lobby();
        break;
    case "start_game":
        ns_start_game();
        break;
}
