arguments.argument:
if (!roomId) exit;

// join_lobby
var name;

if (!ns_is_logged_in()) {
   i18n_show('login_needed')
   exit;
}

lobbyId = roomId
password = ''
if (needPass) {
  password = get_string(i18n_get('password'), '')
}

cmd = cmd_init(
    "join_lobby", 1,
    "lobbyId", lobbyId
)

if (password != '') {
    cmd = cmd_add(
        cmd, 1,
        "password", password
    )
}
ns_send(cmd)
