arguments.argument:
//when using lite mode, load game instantly
if (global.enable_lite_mode) {
    global.savenum = 1
    if (file_exists(getSaveFile(global.savenum))) {
        loadGame()
    } else {
        global.death = 0
        global.timestep = 0
        global.difficulty = 1
        room_goto(global.first_stage)
    }
}

buttonText[0] = 'single_player'
buttonText[1] = 'multiple_player'
buttonText[2] = 'exit'
buttonText[3] = 'register'
buttonText[4] = 'login'
buttonText[5] = 'back'

for (i = 0; i < 3; i += 1) {
    inst[i] = instance_create(112 + 216 * i, 450, objControllerButton)
    inst[i].text = buttonText[i]
}

if (debug_mode && !instance_exists(objServerConnector)) {
    instance_create(0, 0, objServerConnector)
}
