arguments.argument:
// debug only
if (ns_is_online_mode()) {
    username = global.debug_host_name
    password = global.debug_host_pass
    if (debug_mode) {
        // send the tcp packet
        cmd = cmd_init(
            'login', 2,
            'name', username,
            'password', password
        )
        ns_send(cmd)
    }
}
