arguments.argument:
if (debug_mode) {
    // send the tcp packet
    cmd = cmd_init(
        'login',
        2,
        'name',
        global.debug_host_name,
        'password',
        global.debug_host_pass
    )
    ns_send(cmd)
    global.online_mode = true
} else {
    ns_login()
    global.online_mode = true
}
