arguments.argument:
if (debug_mode) {
    global.online_mode = false
    global.savenum = 1
    if (file_exists(getSaveFile(global.savenum))) {
        loadGame()
    } else {
        global.death = 0
        global.timestep = 0
        global.difficulty = 1
        room_goto(global.first_stage)
    }
}
