arguments.argument:
jumpPressed = keyboard_check_pressed(global.jumpbutton)
shootPressed = keyboard_check_pressed(global.shootbutton)
prev = keyboard_check_pressed(global.leftbutton) || keyboard_check_pressed(global.upbutton)
next = keyboard_check_pressed(global.rightbutton) || keyboard_check_pressed(global.downbutton)

if (!ns_is_online_mode()) {
    // single player
    if (jumpPressed) {
        music_play(sndJump)
        room_goto(rMenu)
    }
} else {
    // multiple player
    if (status == CHOOSE_MODE) {
        if (prev) {
            music_play(sndJump)
            pointer = gen_sequence(pointer, 0, 2, true)
        }
        if (next) {
            music_play(sndJump)
            pointer = gen_sequence(pointer, 0, 2)
        }
        if (jumpPressed) {
            music_play(sndJump)
            switch (pointer) {
                case ACTION_SINGLE:
                    ns_single_player()
                    break
                case ACTION_MULTIPLE:
                    ns_multiple_player()
                    break
                case ACTION_EXIT:
                    game_end()
                    break
            }
            if (!debug_mode) {
                status = CHOOSE_ACTION
                if (lastuser != '') {
                    pointer = ACTION_LOGIN
                } else {
                    pointer = ACTION_REGISTER
                }
            }
        }
    } else if (status == CHOOSE_ACTION) {
        if (jumpPressed) {
            music_play(sndJump)
            switch (pointer) {
                case ACTION_REGISTER:
                    ns_register()
                    break
                case ACTION_LOGIN:
                    ns_login(lastuser)
                    break
                case ACTION_RETURN:
                    status = CHOOSE_MODE
                    pointer = ACTION_MULTIPLE
                    break
            }
        }
        if (shootPressed) {
            music_play(sndDJump)
            status = CHOOSE_MODE
            pointer = ACTION_MULTIPLE
        }
        if (prev) {
            music_play(sndJump)
            pointer = gen_sequence(pointer, 0, 2, true)
        }
        if (next) {
            music_play(sndJump)
            pointer = gen_sequence(pointer, 0, 2)
        }
    }
}
