arguments.argument:
if (global.triggered == key && !keyTriggered) {
    keyTriggered = true;
}

if (image_index != 0) exit;
if (key != 0 && !keyTriggered) exit;

shouldTrigger =
    place_meeting(x, y, bullet) ||
    (place_meeting(x, y, player) && key_check_pressed(global.shootbutton));

if (global.triggered != trg && shouldTrigger) {
    ns_send_instance(
      2,
      "trg", trg, 
      "snd", snd
    );
    event_user(EVENT_TCP);
}
