arguments.argument:
var i;

if (global.triggered != trg[1] && !triggered[1]) {
    triggered[1] = true
    if (snd[1]) {
        music_play(snd[1])
    }

    if (ns_is_in_game() && !noSync) {
        map = cmd_init()
        trgList = cmd_add_list(map, 't')
        sndList = cmd_add_list(map, 'snd')
        timeList = cmd_add_list(map, 'time')
        for (i = 1; i <= maxTriggers; i += 1) {
            if (trg[i] != 0) {
                cmd_list_add(trgList, 1, trg[i])
                cmd_list_add(sndList, 1, snd[i])
                cmd_list_add(timeList, 1, time[i])
            } else {
                break;
            }
        }
        ns_send_instance_direct(map)
    }
    event_user(EVENT_TCP)
}
