arguments.argument:
for (i = 1; i <= maxTriggers; i += 1) {
    if (triggered[i]) {
        global.triggered = trg[i]
        with (objTriggerable) {
            if (trg == global.triggered) {
                triggered = true
                event_user(EVENT_TRIGGER)
            }
        }

        time[i] -= 1
        if (time[i] == -1) {
            if (trg[i + 1] > 0) {
                triggered[i + 1] = true
                if (snd[i + 1]) {
                    music_play(snd[i + 1])
                }
            } else {
                instance_destroy()
            }
        }
    }
}
