arguments.argument:
// this is almost the same with pathFree
// but it can be triggered multiple times sequentially
// see rTraps for creation code help

depth = 1000002;

ensure('trg[1]', trg[1])
ensure('pth[1]', pth[1])
ensure('spd[1]', spd[1])

// maximum trigger times
for (i = 1; i < 10; i += 1) {
    if (trg[i] == 0 && pth[i] == 0) {
        maxTraps = i - 1
    }
}

event_inherited()
