arguments.argument:
if (global.triggered == trg && !flag) {
    path_start(pth, spd, enda, 0)
    if (scl != 0) {
        path_scale = scl
    }
    flag = 1
}

if (path_position > 0 && path_position < 1 && place_meeting(x, y, player)) {
    with (player) {
        killPlayer()
    }
}

//  if player dies during when the block is still moving
//  it continue moving along the previous direction
//  you can choose to disable this by deleting this event
if (!instance_exists(player)) {
    if (move && path_position != 1 && path_endaction == PATH_ACTION_STOP) {
        speed = path_speed
        path_end()
        move = false
    }
}
