arguments.argument:
if (triggered) {
    if (timer < time) {
        timer += 1
        theta += angle / time
    }
    image_angle = theta
    dx = -(cx * cos(theta * pi / 180) + cy * sin(theta * pi / 180))
    dy = -(-cx * sin(theta * pi / 180) + cy * cos(theta * pi / 180))
    x = xstart + cx + dx
    y = ystart + cy + dy
}
