arguments.argument:
count = 0;
if(countBoss == true){
    for(i=1;i<=global.boss_number;i+=1){
        count += global.boss[i];
    }
}
if(countItem == true){
    for(i=1;i<=global.item_number;i+=1){
        count += global.item[i];
    }
}
if(count >= total){
    visible = true;
}
else{
    visible = false;
}
