arguments.argument:
with (player) {
    instance_destroy()
}

instance_create(0, 0, warpWaitingBG)

// load game
if (dif == -1 && !file_exists(getSaveFile(global.savenum))) {
    room_restart()
} else {
    if (dif != -1) {
        global.difficulty = dif
        if (file_exists(getSaveFile(global.savenum))) {
            shouldDelete = show_question('delete_save')
            if (shouldDelete) {
                file_delete(getSaveFile(global.savenum))
            } else {
                room_restart()
            }
        }
        global.death = 0
        global.timestep = 0
        roomTo = global.first_stage
    } else {
        roomTo = rLoad
    }
    ns_send_wait(
        'warp',
        string(room) + '-start-' + string(dif),
        2,
        'room', room,
        'roomTo', roomTo
    )
}
