arguments.argument:
if (!noSave) {
    global.__sync_position = true
}

player.speed = 0

//load game
if(dif == 4){
    if (!file_exists(get_save_file(global.savenum))){
        with(player){
            instance_destroy();
        }
        room_restart();
    }
    else{
        with(player) {
            instance_destroy()
        }
        room_goto(rLoad);
    }
}
//new game
else{
    global.difficulty = dif;
    if(file_exists(get_save_file(global.savenum)) == true){
        file_delete(get_save_file(global.savenum));
    }
    global.death = 0;
    global.timestep = 0;
    roomTo = global.first_stage;
    room_goto(roomTo);
    with(player){
        instance_destroy();
    }
}
